              description: "A Cozy Place for Book Lovers, Readers And Writers",
              icon: "https://literature.cafe/pictrs/image/941180b9-e8ad-436d-8aa7-45e321b30ed6.webp",
              id: 1,
              inbox_url: "https://literature.cafe/inbox",
